package edu.memphis.ccrg.lida.framework.gui;

/**
 * Event generated by the framework to be handled by the GUI.
 * 
 * @author Javier snaider
 * 
 */
public class FrameworkGuiEvent {
	public static final int FRAMEWORK=0;
	public static final int PAM = 10;
	public static final int TEM = 20;
	public static final int DM = 30;
	public static final int WORKSPACE = 40;
	public static final int PERCEPTUAL_BUFFER = 41;
	public static final int EPISODIC_BUFFER = 42;
	public static final int BROADCAST_QUEUE = 43;
	public static final int CSM = 44;
	public static final int SBCODELETS = 45;
	public static final int GLOBAL_WORKSPACE = 50;
	public static final int PROCEDURAL_MEMORY = 60;
	public static final int ACTION_SELECTION = 70;
	public static final int ENVIRONMENT = 1;

	private int moduleId;
	private String message;
	private Object data;

	/**
	 * @param moduleId
	 * @param message
	 * @param data
	 */
	public FrameworkGuiEvent(int moduleId, String message, Object data) {
		this.moduleId = moduleId;
		this.message = message;
		this.data = data;
	}

	/**
	 * @return the moduleId
	 */
	public int getModuleId() {
		return moduleId;
	}

	/**
	 * @return the message
	 */
	public String getMessage() {
		return message;
	}

	/**
	 * @return the data
	 */
	public Object getData() {
		return data;
	}
}
