package edu.memphis.ccrg.lida.framework.gui.events;

import edu.memphis.ccrg.lida.framework.ModuleName;

/**
 * Event generated by the framework to be handled by the GUI.
 * 
 * @author Javier snaider
 * 
 */
public class FrameworkGuiEvent {

	/**
	 * Module sending the event
	 */
	private ModuleName module;
	
	/**
	 * Message describing the nature of the event's content
	 */
	private String message;
	
	/**
	 * Content being sent
	 */
	private Object content;

	/**
	 * @param m
	 * @param message
	 * @param content
	 */
	public FrameworkGuiEvent(ModuleName m, String message, Object content) {
		this.module = m;
		this.message = message;
		this.content = content;
	}

	public void setModule(ModuleName m){
		module = m;
	}
	/**
	 * @return the moduleId
	 */
	public ModuleName getModule() {
		return module;
	}

	public void setMessage(String m){
		message = m;
	}
	/**
	 * @return the message
	 */
	public String getMessage() {
		return message;
	}

	public void setContent(Object o){
		content = o;
	}
	/**
	 * @return the data
	 */
	public Object getContent() {
		return content;
	}
}
